import React,{useState} from 'react';
import {useHistory} from 'react-router-dom';
import M from "materialize-css"

const Createpost=()=>{
	const history=useHistory()
	const[title,setTitle]=useState("")
	const[body,setBody]=useState("")
	const[image,setImage]=useState("")

	const PostDetails=()=>{
		const data=new FormData()
		data.append("file",)
		data.append("upload_preset","insta-clone")
		data.append("cloud_name","vivekscloud")
		fetch("https://api.cloudinary.com/v1_1/vivekscloud/image/upload",{
			method:"post",
			body:data
		}).then(res=>res.json())
		.then(data=>{
			console.log(data)
		}).catch(err=>{
			console.log(err)
		})
	}
	return(
		<div>
			<div className="card input-field" style={{maxWidth:"550px", margin:"26px auto", padding:"30px", textAlign:"center"}}>
				<input type="text" placeholder="Title" value={title} onChange={(event)=>setTitle(event.target.value)}/>
				<input type="text" placeholder="Body" value={body} onChange={(event)=>setTitle(event.target.body)}/>
				<div className="file-field input-field">
			      <div className="btn #1976d2 blue darken-2">
			        <span>File</span>
			        <input type="file" onChange={(event)=>setImage(event.target.files[0])}/>
			      </div>
			      <div className="file-path-wrapper">
			        <input className="file-path validate" type="text"/>
			      </div>
			     </div>
			     <a className="waves-effect waves-light btn #1976d2 blue darken-2 createpostsubmit">button</a>
			</div>

		</div>
		)
}


export default Createpost
